### 1. æ¤œç´¢ã‚¨ãƒ³ã‚¸ãƒ³ã®ãƒ‘ãƒ•ã‚©ãƒ¼ãƒãƒ³ã‚¹æ”¹å–„

```javascript
// æ¤œç´¢æ©Ÿèƒ½ã®ãƒ‘ãƒ•ã‚©ãƒ¼ãƒãƒ³ã‚¹æœ€é©åŒ–
class OptimizedSearchIndex {
  constructor() {
    this.index = {};         // è»¢ç½®ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹
    this.documents = {};     // ãƒ‰ã‚­ãƒ¥ãƒ¡ãƒ³ãƒˆæ ¼ç´
    this.embeddings = {};    // ãƒ™ã‚¯ãƒˆãƒ«åŸ‹ã‚è¾¼ã¿
    this.metadata = {};      // ãƒ¡ã‚¿ãƒ‡ãƒ¼ã‚¿
  }

  // ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã®æ§‹ç¯‰
  buildIndex(issues) {
    console.time('Index Building');

    // ãƒ‰ã‚­ãƒ¥ãƒ¡ãƒ³ãƒˆå‡¦ç†ã‚’è¤‡æ•°ã®ãƒãƒ£ãƒ³ã‚¯ã«åˆ†å‰²
    const chunkSize = 50;
    const chunks = this.chunkArray(issues, chunkSize);

    // å„ãƒãƒ£ãƒ³ã‚¯ã‚’å‡¦ç†
    chunks.forEach((chunk, i) => {
      // å‡¦ç†é–‹å§‹æ™‚é–“
      const startTime = performance.now();

      // ãƒãƒ£ãƒ³ã‚¯ã‚’ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹åŒ–
      this.indexChunk(chunk);

      // å‡¦ç†çµ‚äº†æ™‚é–“
      const endTime = performance.now();
      console.log(`Indexed chunk ${i+1}/${chunks.length} in ${(endTime - startTime).toFixed(2)}ms`);
    });

    // ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã®æœ€é©åŒ–
    this.optimizeIndex();

    console.timeEnd('Index Building');
    return this;
  }

  // é…åˆ—ã‚’ãƒãƒ£ãƒ³ã‚¯ã«åˆ†å‰²ã™ã‚‹ãƒ¦ãƒ¼ãƒ†ã‚£ãƒªãƒ†ã‚£
  chunkArray(array, size) {
    const chunks = [];
    for (let i = 0; i < array.length; i += size) {
      chunks.push(array.slice(i, i + size));
    }
    return chunks;
  }

  // ãƒãƒ£ãƒ³ã‚¯ã‚’ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹åŒ–
  indexChunk(issues) {
    issues.forEach(issue => {
      // ãƒ‰ã‚­ãƒ¥ãƒ¡ãƒ³ãƒˆã‚’ä¿å­˜
      this.documents[issue.number] = issue;

      // ãƒ¡ã‚¿ãƒ‡ãƒ¼ã‚¿ã‚’æŠ½å‡º
      this.metadata[issue.number] = {
        createdAt: new Date(issue.created_at),
        updatedAt: new Date(issue.updated_at),
        labels: issue.labels?.map(label => label.name) || [],
        author: issue.user?.login,
      };

      // ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹åŒ–ã™ã‚‹ãƒ†ã‚­ã‚¹ãƒˆ
      const text = `${issue.title} ${issue.body || ''}`;

      // ãƒ†ã‚­ã‚¹ãƒˆã‚’å‡¦ç†
      this.processText(issue.number, text);

      // åŸ‹ã‚è¾¼ã¿ã‚’è¨ˆç®—ï¼ˆãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰ã§ï¼‰
      setTimeout(() => {
        this.computeEmbedding(issue.number, text);
      }, 0);
    });
  }

  // ãƒ†ã‚­ã‚¹ãƒˆã‚’å‡¦ç†ã—ã¦ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã«è¿½åŠ 
  processText(id, text) {
    // ãƒ†ã‚­ã‚¹ãƒˆã‚’ãƒˆãƒ¼ã‚¯ãƒ³åŒ–
    const tokens = this.tokenize(text);

    // è»¢ç½®ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã«è¿½åŠ 
    tokens.forEach(token => {
      if (!this.index[token]) {
        this.index[token] = { df: 0, docs: {} };
      }

      if (!this.index[token].docs[id]) {
        this.index[token].docs[id] = { tf: 0, positions: [] };
        this.index[token].df++;
      }

      this.index[token].docs[id].tf++;
    });
  }

  // ãƒ†ã‚­ã‚¹ãƒˆã‚’ãƒˆãƒ¼ã‚¯ãƒ³åŒ–ã™ã‚‹é–¢æ•°
  tokenize(text) {
    // ã‚ˆã‚Šæ´—ç·´ã•ã‚ŒãŸãƒˆãƒ¼ã‚¯ãƒ³åŒ–ã‚’ä½¿ç”¨ã™ã‚‹ã“ã¨ã‚’æ¨å¥¨
    // ã“ã“ã§ã¯ç°¡æ˜“çš„ãªå®Ÿè£…
    return text.toLowerCase()
      .replace(/[^a-z0-9\s]/g, ' ')
      .split(/\s+/)
      .filter(token => token.length > 1);
  }

  // åŸ‹ã‚è¾¼ã¿ã‚’è¨ˆç®—
  computeEmbedding(id, text) {
    // è»½é‡ãªåŸ‹ã‚è¾¼ã¿ãƒ¢ãƒ‡ãƒ«ã‚’ä½¿ç”¨
    // å®Ÿéš›ã®å®Ÿè£…ã§ã¯ã€ã‚ˆã‚Šé«˜åº¦ãªãƒ¢ãƒ‡ãƒ«ãŒå¿…è¦
    this.embeddings[id] = simpleTextEmbedding(text);
  }

  // ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã‚’æœ€é©åŒ–
  optimizeIndex() {
    // ä½é »åº¦ãƒ»é«˜é »åº¦ãƒˆãƒ¼ã‚¯ãƒ³ã®ãƒ•ã‚£ãƒ«ã‚¿ãƒªãƒ³ã‚°
    const totalDocs = Object.keys(this.documents).length;
    const minDf = Math.max(2, Math.floor(totalDocs * 0.01));
    const maxDf = Math.floor(totalDocs * 0.9);

    // ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã®æœ€é©åŒ–
    Object.keys(this.index).forEach(token => {
      const entry = this.index[token];

      // å°‘ãªã™ãã‚‹/å¤šã™ãã‚‹ãƒˆãƒ¼ã‚¯ãƒ³ã‚’é™¤å¤–
      if (entry.df < minDf || entry.df > maxDf) {
        delete this.index[token];
        return;
      }

      // TF-IDFã‚¹ã‚³ã‚¢ã®äº‹å‰è¨ˆç®—
      const idf = Math.log(totalDocs / entry.df);

      Object.entries(entry.docs).forEach(([docId, docData]) => {
        // TF-IDFã‚¹ã‚³ã‚¢ã‚’è¨ˆç®—
        docData.tfidf = docData.tf * idf;
      });
    });
  }

  // ã‚¯ã‚¨ãƒªå‡¦ç†
  search(query, options = {}) {
    console.time('Search');

    const { useSemanticSearch = true, maxResults = 20 } = options;

    // ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰æ¤œç´¢
    const keywordResults = this.keywordSearch(query, options);

    // ã‚»ãƒãƒ³ãƒ†ã‚£ãƒƒã‚¯æ¤œç´¢ï¼ˆã‚ªãƒ—ã‚·ãƒ§ãƒ³ï¼‰
    let semanticResults = [];
    if (useSemanticSearch) {
      semanticResults = this.semanticSearch(query, options);
    }

    // çµæœã‚’çµ±åˆ
    const combinedResults = this.combineResults(keywordResults, semanticResults, options);

    console.timeEnd('Search');

    return combinedResults.slice(0, maxResults);
  }

  // ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰æ¤œç´¢
  keywordSearch(query, options) {
    // ã‚¯ã‚¨ãƒªã‚’ãƒˆãƒ¼ã‚¯ãƒ³åŒ–
    const tokens = this.tokenize(query);

    if (tokens.length === 0) return [];

    // å„ãƒˆãƒ¼ã‚¯ãƒ³ã®ã‚¹ã‚³ã‚¢ã‚’è¨ˆç®—
    const docScores = {};

    tokens.forEach(token => {
      if (!this.index[token]) return;

      Object.entries(this.index[token].docs).forEach(([docId, docData]) => {
        if (!docScores[docId]) {
          docScores[docId] = 0;
        }

        // TF-IDFã‚¹ã‚³ã‚¢ã‚’åŠ ç®—
        docScores[docId] += docData.tfidf;
