      .sort((a, b) => b.score - a.score);
  }

  // ã‚»ãƒãƒ³ãƒ†ã‚£ãƒƒã‚¯æ¤œç´¢
  semanticSearch(query, options) {
    // ã‚¯ã‚¨ãƒªã®åŸ‹ã‚è¾¼ã¿ã‚’è¨ˆç®—
    const queryEmbedding = simpleTextEmbedding(query);

