    // ã‚»ãƒãƒ³ãƒ†ã‚£ãƒƒã‚¯çµæœã‚’è¿½åŠ ã¾ãŸã¯çµ±åˆ
    semanticResults.forEach(result => {
      const id = result.issue.number;
      if (combinedMap.has(id)) {
        // æ—¢å­˜ã‚¨ãƒ³ãƒˆãƒªã‚’æ›´æ–°
        const existing = combinedMap.get(id);
        existing.semanticScore = result.score;
        // æœ€çµ‚ã‚¹ã‚³ã‚¢ã‚’é‡ã¿ä»˜ã‘ã—ã¦å†è¨ˆç®—
        existing.finalScore = (existing.keywordScore * 0.7) + (result.score * 0.3);
      } else {
        // æ–°ã—ã„ã‚¨ãƒ³ãƒˆãƒªã‚’è¿½åŠ 
        combinedMap.set(id, {
          ...result,
          keywordScore: 0,
          semanticScore: result.score,
          finalScore: result.score * 0.3 // ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰ãƒãƒƒãƒãŒãªã„å ´åˆã¯é‡ã¿ã‚’ä¸‹ã’ã‚‹
        });
      }
    });

    // ãƒãƒƒãƒ—ã‚’é…åˆ—ã«å¤‰æ›ã—ã¦ä¸¦ã¹æ›¿ãˆ
    return Array.from(combinedMap.values())
      .sort((a, b) => b.finalScore - a.finalScore);
  }
}
