// æ”¹å–„ã•ã‚ŒãŸè‡ªå‹•ãƒªãƒ³ã‚¯å‡¦ç†é–¢æ•°
export function applyZettelkastenLinks(
  body: string, 
  keywords: Map<string, number>, 
  currentIssueNumber: number
): string {
  const config = loadZettelkastenSettings();
  if (!config.enabled || !config.autoLinkKeywords) return body;

  // æ—¢å­˜ã® [[...]] ãƒªãƒ³ã‚¯ã‚’æ¤œå‡ºã™ã‚‹ãŸã‚ã®æ­£è¦è¡¨ç¾
  const wikiLinkRegex = /\\[\\[(.*?)\\]\\]/g;

  // æ—¢å­˜ãƒªãƒ³ã‚¯ã®ä½ç½®ã‚’è¨˜éŒ²
  const existingLinks = [];
  let match;
  while ((match = wikiLinkRegex.exec(body)) !== null) {
    existingLinks.push({
      start: match.index,
      end: match.index + match[0].length,
      text: match[0]
    });
  }

  // ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰ã‚’é•·ã•ã§ã‚½ãƒ¼ãƒˆ (é•·ã„é †)
  const sortedKeywords = Array.from(keywords.entries())
    .sort((a, b) => b[0].length - a[0].length);

  // å‡¦ç†æ¸ˆã¿ã®ä½ç½®ã‚’è¿½è·¡
  let result = '';
  let lastIndex = 0;

  // ã‚¹ãƒãƒ¼ãƒˆãƒªãƒ³ã‚¯ç”Ÿæˆã®ãŸã‚ã®çŠ¶æ…‹ç®¡ç†
  const linkedKeywords = new Set();  // æ—¢ã«ãƒªãƒ³ã‚¯æ¸ˆã¿ã®ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰
  const maxLinksPerKeyword = 2;      // å„ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰ã®æœ€å¤§ãƒªãƒ³ã‚¯æ•°
  const keywordLinkCount = new Map(); // ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰ã”ã¨ã®ãƒªãƒ³ã‚¯æ•°ã‚«ã‚¦ãƒ³ãƒˆ

  for (let i = 0; i < body.length;) {
    // ã“ã®ä½ç½®ãŒæ—¢å­˜ãƒªãƒ³ã‚¯å†…ã‹ãƒã‚§ãƒƒã‚¯
    let insideExistingLink = false;
    for (const link of existingLinks) {
      if (i >= link.start && i < link.end) {
        // æ—¢å­˜ãƒªãƒ³ã‚¯å†…ãªã‚‰ã€ãƒªãƒ³ã‚¯å…¨ä½“ã‚’ã‚¹ã‚­ãƒƒãƒ—
        if (i === link.start) {
          result += link.text;
          lastIndex = link.end;
        }
        i = link.end;
        insideExistingLink = true;
        break;
      }
    }

    if (insideExistingLink) continue;

    // ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰ãƒãƒƒãƒãƒ³ã‚°
    let matched = false;
    for (const [keyword, issueNumber] of sortedKeywords) {
      // è‡ªåˆ†è‡ªèº«ã¸ã®ãƒªãƒ³ã‚¯ã¯ã‚¹ã‚­ãƒƒãƒ—
      if (issueNumber === currentIssueNumber) continue;

      // å„ã‚­ãƒ¼ãƒ¯ãƒ¼ãƒ‰ã®ãƒªãƒ³ã‚¯æ•°ã‚’åˆ¶é™
      if (keywordLinkCount.get(keyword) >= maxLinksPerKeyword) continue;

      // å˜èªå¢ƒç•Œãƒã‚§ãƒƒã‚¯ã®ãŸã‚ã®ç°¡æ˜“ç‰ˆ
      const remainingText = body.slice(i);
      const keywordLower = keyword.toLowerCase();
      const lowerText = remainingText.toLowerCase();

      if (lowerText.startsWith(keywordLower)) {
        const charBefore = i > 0 ? body[i - 1] : ' ';
        const charAfter = body[i + keyword.length] || ' ';

        // å˜èªå¢ƒç•Œãƒã‚§ãƒƒã‚¯ (ç°¡æ˜“ç‰ˆ)
        if (/\\W/.test(charBefore) && /\\W/.test(charAfter)) {
          // å‰å¾Œã®æ–‡è„ˆã‚’è€ƒæ…®ã—ãŸé–¢é€£æ€§ã‚¹ã‚³ã‚¢ãƒªãƒ³ã‚°ï¼ˆã‚·ãƒ³ãƒ—ãƒ«ãªå®Ÿè£…ï¼‰
          const contextRelevant = isContextRelevant(body, i, keyword);

          if (contextRelevant) {
            // ãƒªãƒ³ã‚¯ã¨ã—ã¦è¿½åŠ 
            result += `[[${body.slice(i, i + keyword.length)}]]`;
            i += keyword.length;
            matched = true;

            // ãƒªãƒ³ã‚¯æ•°ã‚’ã‚«ã‚¦ãƒ³ãƒˆ
            keywordLinkCount.set(keyword, (keywordLinkCount.get(keyword) || 0) + 1);
            break;
